package com.syntax.class8;

public class ForLoop2Review {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	// I want to print 1 3 7 9
		
		
		for(int i=1; i<=9; i+=2) {
			if(i==5) {
				continue;
			}
			System.out.println(i);
		}
	//	Task number3
		for(int j=0; j<=5;j++) {
			for(int i=1; i<=5; i++);
			System.out.print(j+" ");
		}
		System.out.println();
	}
	}



