package com.syntax.class9;

public class ArrayFixedSize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
	int[] nums=new int[3];
	nums[1]=12;
	nums[2]=13;
	System.out.println(nums[2]);
	
	String[] colors=new String[3];
	colors[0]="white";
	colors[1]="pink";
	colors[2]="black";
	System.out.println(colors[0]);
	}

}
